mapper.select:
SELECT 
FROM TB_EVALUATION
WHERE IS_DELETED = 'N'

    AND ORDER_ID = #{params.orderId}


    AND CUSTOMER_ID = #{params.customerId}


    AND STAFF_ID = #{params.staffId}


    AND SCORE = #{params.score}

ORDER BY CREATE_TIME DESC

    OFFSET #{params.page} ROWS
    FETCH NEXT #{params.size} ROWS ONLY

mapper.update:
UPDATE TB_EVALUATION
SET IS_DELETED = 'Y'
WHERE EVAL_ID = #{id}

mapper.select:
SELECT COUNT(*)
FROM TB_EVALUATION
WHERE IS_DELETED = 'N'

    AND ORDER_ID = #{params.orderId}


    AND CUSTOMER_ID = #{params.customerId}


    AND STAFF_ID = #{params.staffId}


    AND SCORE = #{params.score}
